import React from 'react';
import './App.css';

class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {            
            cards : []
        }
    }

    updateInput(key, value) {
        this.setState({
            [key] : value
        })
    }


    addField() {
        let newCard = {
            value : {
               overturned : false
            }
        };

        this.setState({
            cards : [newCard]
        })

        let timeoutID;
    }

    turnCard(id) {
        const cards = [...this.state.cards];

        let index = cards.findIndex((card) => {
            return card.id === id
        });

        cards[index].value.overturned = !cards[index].value.overturned;

        this.updateInput("cards", cards);
    }

    render() {
        return (
            <div className="App">
                <h1 className="app-title">Проверь свою реакцию!</h1>
                <div className='upper'>
                <button
                        className="btn add-btn"
                        onClick={() => this.addField()}
                    >
                        Старт!
                    </button>
                </div>
                <div className="container">


                    <div>
                        {this.state.cards.map(card => {
                            return(
                                <div
                                    key={card.id}
                                    className={"card" + (card.value.overturned ? " overturned" : "")}
                                    onClick={() => this.turnCard(card.id)}
                                >
                                    {card.value.overturned ? card.value.translate : card.value.word}
                                </div>
                            )
                        })}
                    </div>

                </div>
                <div>
                    <output className = 'result'>2</output>
                    </div>
            </div>
        );
    }
}

export default App;
