import React from 'react';
import './App.css';

class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {            
            green : false,
            startTime: '',
            endTime: '',
            rndTime: '',
            result: ''
        }
        this.getGreen = this.getGreen.bind(this)
        this.getRed = this.getRed.bind(this)
    }


getGreen() {
  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  }

  const time = getRandomInt(1000,4000)

  this.setState ({
    startTime: new Date(),
    rndTime: time
  })


  setTimeout(
    () => this.setState({
      green: true
    }), time
  );
}

getRed() {
  if (this.state.green) {
    this.setState({
      green: false
    })
  }

  this.setState({
    result: new Date() - this.state.startTime - this.state.rndTime
  })
}
    render() {
        return (
            <div className="App">
                <h1>Проверь свою реакцию!</h1>
                <br />
                <button onClick = {this.getGreen}>
                  Старт!
                </button>
                <div 
                className ={this.state.green ? 'overturned' : 'container'}
                onClick = {this.getRed}
                >

                </div>
                <div>
                  <h1>
                    2
                  </h1>
                </div>
            </div>
        );
    }
}

export default App;
