import React from 'react';
import './App.css';

class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {            
            green : false
        }
        this.getGreen = this.getGreen.bind(this)
    }


getGreen() {
  setTimeout(
    () => this.setState({
      green: !this.state.green
    }), 2000
  );

}
    render() {
        return (
            <div className="App">
                <h1>Проверь свою реакцию!</h1>
                <br />
                <button onClick = {this.getGreen}>
                  Старт!
                </button>
                <div className ={this.state.green ? 'overturned' : 'container'}>

                </div>
            </div>
        );
    }
}

export default App;
